import React, { useRef, useEffect } from "react";
import { motion, useAnimation, useInView } from "framer-motion";

import Languages from "./Languages";
import ToolsAndPlatforms from "./ToolsAndPlatforms";
import Frameworks from "./Frameworks";
import Databases from "./Databases";

const skillsBackgrounds = [
  "rgba(215, 192, 172, 0.4)", // Languages
  "rgba(172, 215, 192, 0.4)", // Tools & Platforms
  "rgba(192, 172, 215, 0.4)", // Frameworks
  "rgba(172, 206, 215, 0.4)", // Databases
];

const Skills = () => {
  const ref = useRef(null);
  const isInView = useInView(ref, { margin: "-100px" }); // triggers a bit earlier
  const controls = useAnimation();

  const skillBlocks = [
    { Component: Languages, key: "languages" },
    { Component: ToolsAndPlatforms, key: "tools" },
    { Component: Frameworks, key: "frameworks" },
    { Component: Databases, key: "databases" },
  ];

  useEffect(() => {
    if (isInView) {
      let activeIndex = 0;
      controls.start((i) => ({
        scale: i === activeIndex ? 1.1 : 1,
        backgroundColor:
          i === activeIndex ? skillsBackgrounds[i] : "transparent",
        transition: { duration: 0.6 },
      }));

      const interval = setInterval(() => {
        activeIndex = (activeIndex + 1) % skillBlocks.length;
        controls.start((i) => ({
          scale: i === activeIndex ? 1.1 : 1,
          backgroundColor:
            i === activeIndex ? skillsBackgrounds[i] : "transparent",
          transition: { duration: 0.6 },
        }));
      }, 1500);

      return () => clearInterval(interval);
    } else {
      controls.stop();
      controls.start((i) => ({
        scale: 1,
        backgroundColor: "transparent",
        transition: { duration: 0.3 },
      }));
    }
  }, [isInView, controls, skillBlocks.length]);

  return (
    <section className="skills section" id="skills" ref={ref}>
      <h2 className="section_title">Skills</h2>
      <span className="section_subtitle">My Technical Skill Set</span>
      <div className="skills_container container grid">
        {skillBlocks.map(({ Component, key }, index) => (
          <motion.div
            key={key}
            className="skills_content"
            custom={index}
            animate={controls}
            initial={{ scale: 1, backgroundColor: "transparent" }}
          >
            <Component />
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
