import React, { useRef, useEffect } from "react";
import { motion, useAnimation, useInView } from "framer-motion";

import Languages from "./Languages";
import ToolsAndPlatforms from "./ToolsAndPlatforms";
import Frameworks from "./Frameworks";
import Databases from "./Databases";

const skillsBackgrounds = [
  "rgba(215, 192, 172, 0.4)", // Languages
  "rgba(172, 215, 192, 0.4)", // Tools & Platforms
  "rgba(192, 172, 215, 0.4)", // Frameworks
  "rgba(172, 206, 215, 0.4)", // Databases
];

const Skills = () => {
  const ref = useRef(null);
  const isInView = useInView(ref);
  const controls = useAnimation();

  const skillBlocks = [
    <Languages key="languages" />,
    <ToolsAndPlatforms key="tools" />,
    <Frameworks key="frameworks" />,
    <Databases key="databases" />,
  ];

  useEffect(() => {
    if (isInView) {
      // Start looped highlight animation
      (async () => {
        while (true) {
          for (let i = 0; i < skillBlocks.length; i++) {
            await controls.start((custom) =>
              custom === i
                ? {
                    scale: 1.1,
                    backgroundColor: skillsBackgrounds[i],
                    transition: { duration: 0.6 },
                  }
                : {
                    scale: 1,
                    backgroundColor: "transparent",
                    transition: { duration: 0.6 },
                  }
            );
          }
        }
      })();
    } else {
      controls.stop();
      // Reset all blocks when not in view
      controls.start(() => ({
        scale: 1,
        backgroundColor: "transparent",
        transition: { duration: 0.3 },
      }));
    }
  }, [isInView, controls, skillBlocks.length]);

  return (
    <section className="skills section" id="skills" ref={ref}>
      <h2 className="section_title">Skills</h2>
      <span className="section_subtitle">My Technical Skill Set</span>
      <div className="skills_container container grid">
        {skillBlocks.map((Block, index) => (
          <motion.div
            key={index}
            className="skills_content"
            custom={index}
            animate={controls}
            initial={{ scale: 1, backgroundColor: "transparent" }}
          >
            {Block}
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
