import React, { useState } from "react";
import "./about.css";
import AboutImg from "../../assets/Violet.jpeg";
import Info from "./Info";
import WebViewer from "./WebViewer";

const About = () => {
  const [isWebViewerOpen, setIsWebViewerOpen] = useState(false);

  const handleToggleWebViewer = () => {
    setIsWebViewerOpen(!isWebViewerOpen);
  };

  return (
    <section className="about section" id="about">
      <h2 className="section_title">About Me</h2>
      <span className="section_subtitle">My Journey</span>
      <div className="about-container container grid">
        <img src={AboutImg} alt="Photo of Violet" className="about_img" />
        <div className="about_data">
          <Info />
          <p className="about_description">
            Frontend developer, I create web pages with UI / UX user interface,
            I have years of experience and many clients are happy with the
            projects carried out.
          </p>

          <button
            onClick={handleToggleWebViewer}
            className="button button--flex"
          >
            {isWebViewerOpen ? "Close Details" : "View Details"}
          </button>
          {/* Conditionally render WebViewer inside the about_data div */}
          {isWebViewerOpen && (
            <div className="webview-wrapper" style={{ marginTop: "20px" }}>
              <WebViewer />
            </div>
          )}
        </div>
      </div>
    </section>
  );
};
export default About;
