import React, { useState, useEffect } from "react";
import "./about.css";
import AboutImg from "../../assets/Violet.jpeg";
import Info from "./Info";

const About = () => {
  const [showResume, setShowResume] = useState(false);

  const openResume = () => {
    if (window.innerWidth <= 768) {
      // On small screens, open the resume in a new tab
      window.open(
        "https://drive.google.com/file/d/1fFYUB77KEFsH7hQfoUmcTAAs9WISzdq3/view",
        "_blank"
      );
    } else {
      setShowResume(true);
    }
  };

  const closeResume = () => setShowResume(false);

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth <= 768) {
        setShowResume(false);
      }
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <section className="about section" id="about">
      <h2 className="section_title">About Me</h2>
      <span className="section_subtitle">My Journey</span>

      <div className="about_container container grid">
        <img src={AboutImg} alt="Photo of Violet" className="about_img" />

        <div className="about_data">
          <Info />
          <p className="about_description">
            Frontend developer, I create web pages with UI / UX user interface,
            I have years of experience and many clients are happy with the
            projects carried out.
          </p>

          <button onClick={openResume} className="button button--flex">
            Violet's Resume
            <i className="uil uil-file-alt"></i>
          </button>
        </div>
      </div>

      {showResume && (
        <div className="resume-viewer-overlay">
          <button onClick={closeResume} className="resume-close-button">
            <i className="uil uil-times"></i>
          </button>
          <iframe
            src="https://drive.google.com/file/d/1fFYUB77KEFsH7hQfoUmcTAAs9WISzdq3/preview"
            className="resume-iframe"
            allowFullScreen
            title="Violet's Resume"
          ></iframe>
        </div>
      )}
    </section>
  );
};

export default About;
