import React from "react";
import AnimatedSkillBlock from "./AnimatedSkillBlock";

const ToolsAndPlatforms = () => {
  return (
    <AnimatedSkillBlock index={1} bgColor="rgba(172, 215, 192, 0.4)">
      <h3 className="skills_title">Tools & Platforms</h3>
      <div className="skills_box">
        <div className="skills_group">
          {["Git", "Figma", "Linux", "Windows"].map((tool) => (
            <div className="skills_data" key={tool}>
              <i className="bx bx-badge-check"></i>
              <div>
                <h3 className="skills_name">{tool}</h3>
              </div>
            </div>
          ))}
        </div>
      </div>
    </AnimatedSkillBlock>
  );
};

export default ToolsAndPlatforms;
