import { useState } from "react";
import { Document, Page, pdfjs } from "react-pdf";
import "react-pdf/dist/esm/Page/AnnotationLayer.css";
import "react-pdf/dist/esm/Page/TextLayer.css"; // Use consistent ESM path for CSS

// Import the PDF file directly
import resumePdfFile from "../../assets/John-Cv.pdf";

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

export default function PDFViewer(props) {
  const [numPages, setNumPages] = useState();
  const [pageNumber, setPageNumber] = useState(1);
  const [loadError, setLoadError] = useState(null);

  function prevPage() {
    setPageNumber(pageNumber - 1 <= 1 ? 1 : pageNumber - 1);
  }

  function nextPage() {
    setPageNumber(pageNumber + 1 >= numPages ? numPages : pageNumber + 1);
  }

  function onLoadSuccess({ numPages }) {
    setNumPages(numPages);
    setLoadError(null); // Clear any previous error on successful load
  }

  function onDocumentLoadError(error) {
    console.error("Error while loading PDF:", error);
    setLoadError("Failed to load PDF document. Please try again later.");
  }

  return (
    <div>
      <nav>
        <button onClick={prevPage} disabled={pageNumber <= 1}>
          Previous Page
        </button>
        <button
          onClick={nextPage}
          disabled={!numPages || pageNumber >= numPages}
        >
          Next Page
        </button>
      </nav>
      <div className="pages">
        Page {pageNumber} of {numPages || "--"}
      </div>
      {loadError && (
        <div style={{ color: "red", marginTop: "10px" }}>{loadError}</div>
      )}
      <div className="page">
        <Document
          file={resumePdfFile} // Use the imported PDF file
          onLoadSuccess={onLoadSuccess}
          onLoadError={onDocumentLoadError}
          renderMode="canvas"
        >
          <Page key={pageNumber} pageNumber={pageNumber} />
        </Document>
      </div>
    </div>
  );
}
